import type { ReactNode, MouseEvent, HTMLAttributes } from "react";
import styles from "../css/icon.module.css";

/**
 * 纯展示用 SVG 容器，无点击事件、无 cursor、无 hover 效果。
 * 支持可选的无障碍标签 label，会被设置到容器的 aria-label。
 * @param color 指定图标颜色，默认使用 currentColor
 * @param label 可选的无障碍文本描述
 */
export const IconWrapStatic = ({
  color = "currentColor",
  variant = "large",
  label,
  children,
}: {
  color?: string;
  label?: string;
  variant?: "small" | "large";
  children: ReactNode;
}) => {
  const getClassName = () => {
    const baseClass = styles.iconStatic;
    switch (variant) {
      case "small":
        return `${baseClass} ${styles.iconSmall}`;
      case "large":
        return `${baseClass} ${styles.iconLarge}`;
      default:
        return `${baseClass} ${styles.iconSmall}`;
    }
  };
  return (
    <div style={{ color }} className={getClassName()} aria-label={label}>
      {children}
    </div>
  );
};

/**
 * 可点击的图标容器组件
 * - 功能：包裹 SVG 图标，提供点击、尺寸变体与高亮（active）状态。
 * - 高亮：通过 `active` 在视觉上突出当前图标，并设置无障碍 `aria-pressed`。
 * - 变体：`default | tiny | small | large | pulse | wiggle` 控制尺寸与动效。
 * 边界条件：
 * - label：为空时回退到通用描述，保证无障碍可读；
 * - variant：仅接受预设枚举值；
 * - children：应传入有效的 SVG 内容，否则仅渲染容器。
 */
export const IconWrapWithClick = ({
  label,
  color = "currentColor",
  children,
  onClick,
  variant = "small",
  className,
  active = false,
  ...rest
}: {
  label: string;
  color?: string;
  children: ReactNode;
  onClick?: (e?: MouseEvent<HTMLDivElement>) => void;
  variant?: "default" | "tiny" | "small" | "large" | "pulse" | "wiggle";
  className?: string;
  active?: boolean;
} & HTMLAttributes<HTMLDivElement>) => {
  const getClassName = () => {
    const baseClass = styles.actionBtn;

    switch (variant) {
      case "tiny":
        return `${styles.actionBtnTiny} ${styles.actionBtnAnimated}`;
      case "small":
        return `${styles.actionBtnSmall} ${styles.actionBtnAnimated}`;
      case "large":
        return styles.actionBtnLarge;
      case "pulse":
        return styles.actionBtnPulse;
      case "wiggle":
      default:
        return `${baseClass} ${styles.actionBtnAnimated}`;
    }
  };

  return (
    <div
      {...rest}
      style={{ color }}
      className={`${getClassName()} ${active ? styles.actionBtnActive : ""} ${
        className || ""
      }`}
      aria-label={label || "图标按钮"}
      aria-pressed={active || undefined}
      onClick={onClick}
    >
      {children}
    </div>
  );
};

